import React, { useState } from "react";
import Router, { useRouter } from "next/router";
import useSWR from "swr";
import JobCardsLayout from "../components/JobsectionCards";
import Loader from "../components/LoadingLayout";
import fetch from "isomorphic-unfetch";
import JobCards from "../components/CardsLayout";
import { Box, Flex, Link, Text, Input } from "@chakra-ui/core";

const fetcher = async (url) => {
  const res = await fetch(url);
  const data = await res.json();

  if (res.status !== 200) {
    throw new Error("error", data.message);
  }
  return data;
};

export default function FetchData() {
  const [value, setValue] = useState("");
  const { query } = useRouter();
  const { data, error } = useSWR(
    `/api/seniorAPI${query.title ? "?keyword=" + query.title : ""}`,
    fetcher
  );

  if (error) return <div>Failed to load entry level api</div>;
  if (!data) {
    return <Loader>Scraping Senior Level Jobs</Loader>;
  }

  const handleSubmit = async (e) => {
    e.preventDefault();

    Router.push({
      pathname: "/senior",
      query: { title: value },
    });
  };

  //BOX ----> DIV
  return (
    <JobCardsLayout>
      <Box>
        <Text
          color="white"
          m={5}
          textAlign="center"
          fontSize="30px"
          fontFamily="Sen"
        >
          Senior Level Jobs
        </Text>
        <Text
          color="white"
          m={5}
          textAlign="center"
          fontSize="20px"
          fontFamily="Sen"
        >
          Number of Jobs: {data.length}
        </Text>
        <Flex justifyContent="center">
          <form onSubmit={handleSubmit}>
            <Input
              textAlign="center"
              type="text"
              size="lg"
              placeholder="Search job position.."
              value={value}
              onChange={(event) => setValue(event.target.value)}
            ></Input>
          </form>
        </Flex>
        <Flex flexWrap="wrap" justifyContent="center">
          {data.map((res, index) => {
            const { title, company, location, url } = res;
            return (
              <JobCards
                title={title}
                company={company}
                location={location}
                url={url}
                index={index}
              ></JobCards>
            );
          })}
        </Flex>
      </Box>
    </JobCardsLayout>
  );
}
