import React, { useState, useEffect } from "react";
import Router, { useRouter } from "next/router";
import useSWR, { mutate } from "swr";
import JobCardsLayout from "../components/JobsectionCards";
import fetch from "isomorphic-unfetch";
import NextLink from "next/link";
import Loader from "../components/LoadingLayout";
import JobCards from "../components/CardsLayout";
import {
  Box,
  Flex,
  Text,
  Button,
  ButtonGroup,
  Link,
  Input,
} from "@chakra-ui/core";

const fetcher = async (...url) => {
  const res = await fetch(...url);
  const data = await res.json();

  if (res.status !== 200) {
    throw new Error("error", data.message);
  }
  return data;
};

// const SearchBar = ({ searchJob }) => {
//   const [value, setValue] = useState("");
//   const handleChange = (event) => setValue(event.target.value);

//   const handleSubmit = (e) => {
//     e.preventDefault();
//     if (!value) return;
//     searchJob(value);
//     setValue("");
//   };

//   return (
//     <form onSubmit={handleSubmit}>
//       <Input
//         value={value}
//         onChange={handleChange}
//         placeholder="Search Job Title"
//         size="md"
//       ></Input>
//     </form>
//   );
// };

export default function FetchData() {
  const [value, setValue] = useState("");
  const { query } = useRouter();
  const { data, error } = useSWR("/api/juniorAPI", fetcher);
  // const { data, error } = useSWR(
  //   `/api/juniorAPI${query.keyword ? "?keyword=" + query.keyword : ""}`,
  //   fetcher
  // );

  const handleSubmit = async (e) => {
    e.preventDefault();

    mutate("/api/juniorAPI", ["", value], false);

    mutate(
      "/api/juniorAPI",
      // await fetcher("/api/juniorAPI", {
      //   method: "POST",
      //   body: JSON.stringify({ value }),
      // })
      Router.push({
        pathname: "/api/juniorAPI",
        query: { keyword: value },
      })
    );
    setValue("");
  };

  // const handleSubmit = (e) => {
  //   e.preventDefault()

  // }

  console.log("Input value:", data);

  if (error) return <div>Failed to load entry level api</div>;
  if (!data) {
    return <Loader>Scraping Junior Level Jobs</Loader>;
  }

  //BOX ----> DIV
  return (
    <JobCardsLayout>
      <Box>
        <Box p={3} textAlign="center">
          <Text
            color="white"
            textAlign="center"
            fontSize="40px"
            fontFamily="Sen"
          >
            Junior / Entry Level Jobs
          </Text>
        </Box>

        <Text
          color="white"
          m={5}
          textAlign="center"
          fontSize="20px"
          fontFamily="Sen"
        >
          Number of Jobs: {data.length}
        </Text>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={value}
            onChange={(event) => setValue(event.target.value)}
          ></input>
          <Button variantColor="green">Search</Button>
        </form>
        {/* <SearchBar searchJob={searchJob}></SearchBar> */}
        <Flex flexWrap="wrap" justifyContent="center">
          {data.entry_level.map((res, index) => {
            const {
              Job_Position,
              Job_Company_Name,
              Job_Location,
              Job_url,
            } = res;
            return (
              <JobCards
                index={index}
                title={Job_Position}
                company={Job_Company_Name}
                location={Job_Location}
                url={Job_url}
              ></JobCards>
            );
          })}
        </Flex>
      </Box>
    </JobCardsLayout>
  );
}
