//       {data.entry_level.map((res, index) => {
//         const { Job_Position, Job_Company_Name, Job_Location, Job_url } = res;
//         return (
//           <JobCards
//             index={index}
//             title={Job_Position}
//             company={Job_Company_Name}
//             location={Job_Location}
//             url={Job_url}
//           ></JobCards>
//         );
//       })}
//     </Flex>
//   );
// };

export default function FetchData() {
  // const { data, error } = useSWR("/api/juniorAPI", fetcher);
  const { query } = useRouter();
  const { data, error } = useSWR("/api/juniorAPI", fetcher);

  const searchJob = async (text) => {
    mutate(
      "/api/juniorAPI",
      await fetch("/api/junioAPI", {
        method: "GET",
        body: JSON.stringify(text),
      })
    );
  };

  console.log("Input value:", data);

  if (error) return <div>Failed to load entry level api</div>;
  if (!data) {
    return <Loader>Scraping Junior Level Jobs</Loader>;
  }

  //BOX ----> DIV
  return (
    <JobCardsLayout>
      <Box>
        <Box p={3} textAlign="center">
          <Text
            color="white"
            textAlign="center"
            fontSize="40px"
            fontFamily="Sen"
          >
            Junior / Entry Level Jobs
          </Text>
        </Box>

        <Text
          color="white"
          m={5}
          textAlign="center"
          fontSize="20px"
          fontFamily="Sen"
        >
          Number of Jobs: {data.length}
        </Text>

        <SearchBar searchJob={searchJob}></SearchBar>
        <Flex flexWrap="wrap" justifyContent="center">
          {data.entry_level.map((res, index) => {
            const {
              Job_Position,
              Job_Company_Name,
              Job_Location,
              Job_url,
            } = res;
            return (
              <JobCards
                index={index}
                title={Job_Position}
                company={Job_Company_Name}
                location={Job_Location}
                url={Job_url}
              ></JobCards>
            );
          })}
        </Flex>
      </Box>
    </JobCardsLayout>
  );
}
