import React, { Component } from "react";
import fetch from "isomorphic-unfetch";
import useSWR from "swr";
// import "../node_modules/bulma/css/bulma.css";

export default class extends Component {
  constructor(props) {
    super(props);
  }
  static async getInitialProps({ req }) {
    const baseUrl = req ? `${req.protocol}://${req.get("Host")}` : "";
    const res = await fetch(`${baseUrl}/api/jobPosition`);
    const data = await res.json();
    return { positionResult: data.data };
  }

  jobData() {
    const fetcher = (url) => fetch(url).then((res) => res.json);
    const { data, error } = useSWR("/api/entry_level_api", fetcher);

    if (error) return <div>Failed to load entry level api</div>;
    if (!data) return <div>Loading...</div>;

    return (
      <div>
        {data.map((job, index) => {
          const { job_position, company, location, url } = data;
          <article className="job-card">
            <h1>{job_position}</h1>
            <h1>{company}</h1>
            <h1>{location}</h1>
            <h1>{url}</h1>
          </article>;
        })}
      </div>
    );
  }

  jobCards() {
    return this.props.positionResult.mynimoData.map((res, index) => {
      const { Job_Position, Job_Company_Name, Job_Location } = res;
      return (
        <article className="job-card" key={index}>
          <h1>{Job_Position}</h1>
          <h2>{Job_Company_Name}</h2>
          <h2>{Job_Location}</h2>
        </article>
      );
    });
  }

  render() {
    return (
      <div>
        <div>
          <h1>job Section</h1>
          <div className="centered">
            {/* <section className="cards">{this.jobCards()}</section> */}
            <section className="cards">{this.jobData()}</section>
          </div>
        </div>
      </div>
    );
  }
}
